function addOneLike(n){var t=new XMLHttpRequest;t.onreadystatechange=function(){this.readyState===4&&this.status===200&&(document.getElementById("LikeElement"+n).getElementsByClassName("number-of-likes")[0].innerHTML=this.responseText,happyButton(n))};t.open("GET","Messages/IncrementLikes?id="+n,!0);t.send()}function happyButton(n){var t=document.getElementById("LikeElement"+n),i=t.getElementsByTagName("img")[0];i.src="../images/very-happy.png"}