function addOneLike(n){var t=new XMLHttpRequest;t.onreadystatechange=function(){this.readyState===4&&this.status===200&&(document.getElementById("NumberOfLikes"+n).firstElementChild.innerHTML=this.responseText,happyButton(n))};t.open("GET","Messages/IncrementLikes?id="+n,!0);t.send()}function happyButton(n){var t=document.getElementById("NumberOfLikes"+n),i=t.getElementsByTagName("img")[0];i.src="../images/very-happy.png"}